mapper.update:

update post
set category_no = (select no 
				   from category 
				   where blog_id = #{blogId } 
				   	  and name = '미분류')
where category_no = #{no }

choose.when:

select p.no, p.title, p.contents, p.reg_date as regDate, p.category_no as categoryNo
from post p, category c
where p.category_no = c.no
	and c.blog_id = #{id }
    and p.reg_date = (select max(reg_date)
						from post);

choose.otherwise:

select no, title, contents, reg_date as regDate, category_no as categoryNo
from post
where no = #{postNo }
